const initialState = {
  items:[],
  item:{}

};

const reducer =(state=initialState,action)=>{
  switch(action.type){
    case 'POST_ASYNC':
      console.log('reducer');
      return {
        ...state,
        items:action.value
      };

    case 'ADD_POST_ASYNC':
      return {
        ...state,
        //item:action.value
        items:[...state.items,action.value.data]

      };

    default :
    return state;
}

}
export  default reducer;
