import { takeLatest,put,all} from 'redux-saga/effects';


function* addPost(action)
{
  const p=JSON.stringify(action.post)
  try{
    var url="https://jsonplaceholder.typicode.com/posts"
    yield fetch(url,{
      method:'POST',
      body:p,
      headers:{
        'Content_type':'application/json'
      }
    }).then(res=>res.json())
    .then(response => console.log("DOne",JSON.stringify(response)))
  }
  catch(err)
  {
    alert('error'+err)
  }

  yield put({type:'ADD_POST_ASYNC',value:p})
}

function* Post()
{
    console.log("POST is Calling..")
    try{
      const data =  yield fetch('https://jsonplaceholder.typicode.com/posts').then(res=>res.json());
      yield put({type:'POST_ASYNC',value:data})

    }
    catch(err){
      console.log(err);
    }
}



export default function* rootSaga(){
  console.log("root saga")
  yield all([
    yield takeLatest('POST',Post),
    yield takeLatest('ADD_POST',addPost)
  ])

}
