import React,{ Component } from "react";
import {connect } from 'react-redux';
import Innerheader from './Header2'
import ReactDOM from 'react-dom';
import {Provider} from 'react-redux'
import 'bootstrap/dist/css/bootstrap.min.css';
import store from '../store'

class Login extends Component{
  constructor(props){
    super(props);
    this.state={
      email:'',
			password:'',
			error:{},
    }
    this.onChange = this.onChange.bind(this);
    //this.onClick = this.onClick.bind(this);
  }
  onChange(e){
	  this.setState({[e.target.name]:e.target.value});
  }
  // handleClick() {
  //   ReactDOM.render(<Provider store={store}><Innerheader /></Provider>, document.getElementById('root'));
	// }
	handleClick(e){
		e.preventDefault();
		if(this.state.email && this.state.password !=null){
			const logindata={
				email:this.state.email,
				password:this.state.password
			}
			this.props.loginUser(logindata);
		}else alert ('user name or password can not be empty');

	}

  render(){
	  return(
	    <div>
		    <h1>Login form</h1>
		    <form onSubmit={this.onSubmit} >
		      <div>
			      <label>User name </label><br/>
			      <input type="text" name="email"   onChange={this.onChange}/>
			    </div> <br/>
		      <div>
						<label>Password </label><br/>
						<input type="text" name="password" onChange={this.onChange}/>
					</div>
					<br/>
					<button type="button" onClick={this.handleClick}>Submit</button>
					<br/>
		    </form>
	    </div>
	  );
  }
}

const mapDispatchToProps = dispatch =>{
  return {
    loginUser:(logindata)=>dispatch({type:'LOGIN_USER',logindata})
  }
}

export default connect(mapDispatchToProps)(Login);
