import React,{Component} from 'react';
import { connect } from 'react-redux';
import ReactDOM from 'react-dom';
import {Provider} from 'react-redux'
import Posts from './Posts';
import store from '../store'

class Update extends React.Component{
  constructor(props){
    super(props);
    this.state={
      title:'',
      body:''
    }
    this.post=this.post.bind(this);
    this.handleEdit = this.handleEdit.bind(this);
  }

  post()
  {
    ReactDOM.render(<Provider store={store} ><Posts /></Provider>, document.getElementById('root'));
  }


  onChange=(e) => {
      this.setState({[e.target.name]:e.target.value});
  }


  handleEdit = (e) => {

    e.preventDefault();
    const newTitle = this.state.title;
    const newBody = this.state.body;
    const data = {
      id:this.props.id,
      newTitle,
      newBody
    }
    this.props.update(data);
  }


  render() {
    return (
      <div>
        {this.props.id}
        <form >
          <input type="text" onChange={this.onChange}
            placeholder="Enter Post Title" /><br /><br />
          <textarea  rows="5" onChange={this.onChange}
            cols="28" placeholder="Enter Post" /><br /><br />
          <button onClick={this.handleEdit}>Update</button>
          <button onClick={this.post}>Posts</button>
        </form>
      </div>
    );
  }
}

const mapStateToProps = state =>{
  return {
    items:state.items,

  }
}



const mapDispatchToProps = dispatch =>{
  return{
    update:(data)=>dispatch({type:'UPDATE',data})
  };
};


export default connect(mapDispatchToProps)(Update);
