import React,{Component} from 'react';
import { connect } from 'react-redux';


 class Update extends React.Component{
  constructor(props){
    super(props);
    this.state={
      title:'',
      body:''
    }
  }


  onChange=(e) => {
      this.setState({[e.target.name]:e.target.value});


  }
  handleEdit = (e) => {

      e.preventDefault();
      const newTitle = this.state.title;
      const newBody = this.state.body;
      const data = {
        newTitle,
        newBody
      }
      this.props.update({ type: 'UPDATE', id: this.props.post.id, data: data })
  }


  render() {
  return (
  <div>
    <form onSubmit={this.handleEdit}>
      <input required type="text" onChange={this.onChange}
          placeholder="Enter Post Title" /><br /><br />
      <textarea required rows="5" onChange={this.onChange}
          cols="28" placeholder="Enter Post" /><br /><br />
      <button>Update</button>
    </form>
  </div>
  );
  }
  }
export default connect()(Update);
