import {FETCH_POST, NEW_POST} from './type';


export const fetchposts=()=> dispatch=>{
console.log('fetching..')
  fetch('https://jsonplaceholder.typicode.com/posts')
            .then(res => res.json())
            .then(posts =>dispatch({
              type:FETCH_POST,
              payload:posts
            }));

  }
