import React,{ Component } from "react";
import {connect} from 'react-redux';
import Update from './update';


class Posts extends Component{

    componentDidMount(){
        this.props.fetchPost();
    }



    render(){
        const postitems = this.props.posts.map(post =>
            (<div key={post.id}>
                <h3>{post.id}||<button type="button" onClick={()=>this.props.Delete(post.id)}>delete</button></h3>
                <h3>{post.title}||<button type="button" onClick={()=>this.props.update(post.id)}>UPDATE</button></h3>
                <p>{post.body}</p><br/>
            </div>))
        return(
            <div>
                <h1>Posts</h1>
                {postitems}
                <Update/>
            </div>
        );
    }


}

const mapStateToProps=state =>({
    posts:state.posts.items,


});

const mapDispatchToProps =  dispatch =>{
    return{
        fetchPost:() => dispatch({type:'POST'}),
        Delete:(id) => dispatch({type:'DELETE',id}),
        update:(id) => dispatch({type:'UPDATE',id})

    };
};
export default  connect(mapStateToProps,mapDispatchToProps)(Posts);
