import React,{ Component } from "react";
import {connect} from 'react-redux';
import Update from './update';
import ReactDOM from 'react-dom';
import {Provider} from 'react-redux'

import store from '../store'


class Posts extends Component{
    constructor(props){
        super(props);
        this.state={
            showComponent:false
        }

        this.onUpdate = this.onUpdate.bind(this);
    }

    componentDidMount(){
        this.props.fetchPost();
    }

    onUpdate(){
        this.setState({
            showComponent:true
        });
        console.log('update is callling')
        //return(<Update/>)
    }

    demo(id)
    {
        ReactDOM.render(<Provider store={store}><Update /></Provider>, document.getElementById('btn'));
    }

    render(){
        const postitems = this.props.posts.map(post =>
            (<div key={post.id}>
                <h3>{post.id}||</h3>
                <h3>{post.title}></h3>
                <p>{post.body}</p><br/>
                <div>
                    <button type="button" onClick={()=>this.props.Delete(post.id)}>delete</button>
                    <button type="button"  id="btn" onClick={this.demo}>UPDATE</button>
                    {this.state.showComponent?<Update/>:null}


                </div>
            </div>))
        return(
            <div>
                <h1>Posts</h1>
                {postitems}

            </div>
        );
    }


}

const mapStateToProps=state =>({
    posts:state.posts.items,


});

const mapDispatchToProps =  dispatch =>{
    return{
        fetchPost:() => dispatch({type:'POST'}),
        Delete:(id) => dispatch({type:'DELETE',id}),
        update:(id) => dispatch({type:'UPDATE',id})

    };
};
export default  connect(mapStateToProps,mapDispatchToProps)(Posts);
