import React,{ Component } from "react";
import {connect } from 'react-redux';
import 'bootstrap/dist/css/bootstrap.min.css';
import InnerHeader from './Header2'
import ReactDOM from 'react-dom';
import {Provider} from 'react-redux'
import store from '../store'
import Posts from './Posts'
import './Post.css'

class PostForm extends Component{
  constructor(props){
	  super(props);
	  this.state = {
      title:null,
			body:null,
		//postImage:null
		}
		this.onChange = this.onChange.bind(this);
		this.onClick = this.onClick.bind(this);

  }

  onChange(e){
	  this.setState({[e.target.name]:e.target.value});
  }

  onClick(e){
		e.preventDefault();
		if(this.state.title && this.state.body!=null){
			const post = {
				title:this.state.title,
				body : this.state.body,
			//	postImage:this.state.body
			}
			this.props.createPost(post);
			ReactDOM.render(<Provider store={store} ><Posts /></Provider>, document.getElementById("root"));



		}
		else alert('Title or Body can not be Empty')
	}

  render(){
	  return(
	    <div className="div1">
				<center>
					<h3>PostForm</h3><br/><hr/>
					<form onSubmit={this.onSubmit} >
						<div>
							<label>Title: </label>
							<input type="text" name="title"   onChange={this.onChange} value={this.state.title}/>
						</div> <br/>
						<div>
							<label>Body: </label>
							<textarea type="text" rows="3" cols="30" name="body" onChange={this.onChange} /><br/>
						</div>
						{/* <label> Image </label><br/>
						<input type="file" name="postImage" />
						<br/> */}
						<button type="submit"  onClick= {this.onClick}>Submit</button>
						<br/>
		    	</form>
				</center>
	    </div>
	  );
  }
}

const mapStateToProps = state =>{
	return {
		items:state.items,
	}
}

const mapDispatchToProps =  dispatch =>{
	return{
		createPost:(post) =>dispatch({type:'ADD_POST',post})
	};
};

export default connect(mapStateToProps,mapDispatchToProps)(PostForm);
