import { takeLatest,takeEvery,put,all} from 'redux-saga/effects';

function* addPost(action)
{
	const post=JSON.stringify(action.post)


	try{
		var url="http://localhost:3002/posts/"
		yield fetch(url,{
			method:'POST',
			body:post,
			headers: {'Content-Type':'form-data'},
		})
		.then(res=>res.json())
		.then(response => console.log("POST Done",response))
	}

	catch(err)
	{
		alert('error'+err)
	}

	yield put({type:'ADD_NEW_POST_ASYNC',value:post})
//	window.location.reload('root')
}

function* Post()
{
	console.log("POST is Calling..")
	try{
		const data =  yield fetch("http://localhost:3002/posts",{
			method:'GET',
			headers: {'Content-Type':'application/json'},

		}
	).then(res=>res.json());
		yield put({type:'POST_ASYNC',value:data})
	}

	catch(err){
		console.log(err);
	}
}

function* deletePost(action)
{
	console.log('delete is calling from saga..',action._id);

	fetch('http://localhost:3002/posts/'+action._id,{
		method: 'delete',
	})
	.then(res=>res.json())
	.then(response => console.log("Delete Done",response))

	yield put({type:'DELETE_POST'})
	//window.location.reload('root')

}

function* update(action){
	console.log('update is from generator function');

	try{
		alert(action.data.id)

		yield fetch('http://localhost:3002/posts/'+action.data.id,{
			method: 'PATCH',
			body:[{"propName":"title","value":action.data.title},{"propName":"body","value":action.data.body}],
			headers: {'Content-Type':'application/json'},
		})
		.then(res=>res.json())
		.then(response => console.log("PUT Done",response))
	}

	catch(err){

		alert('error'+err)
	}

	yield put({type:'UPDATA_ASYNC'})
	//window.location.reload('root')

}

export default function* rootSaga(){
	console.log("root saga")
	yield all([
		yield takeLatest('POST',Post),
		yield takeEvery('ADD_POST',addPost),
		yield takeEvery('DELETE',deletePost),
		yield takeEvery('UPDATE',update)
	])
}
