import React,{ Component } from "react";
import {connect } from 'react-redux';
import {Provider} from 'react-redux'
import ReactDOM from 'react-dom';
import store from '../store'
import Login from "./Login";


class Signup extends Component{
  constructor(props){
    super(props);
    this.state={
      userName:'',
      password:''
    }
    this.onChange = this.onChange.bind(this);
   // this.handleClick = this.handleClick.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }


  onChange(e){
	  this.setState({[e.target.name]:e.target.value});
  }

  // handleClick() {
  //   ReactDOM.render(<Provider store={store}><Login /></Provider>, document.getElementById('root'));
  // }

  onSubmit(e){
    e.preventDefault();
    if(this.state.userName && this.state.password != null)
    {
      const userData ={
        userName:this.state.userName,
        password:this.state.password
      }
      this.props.createUser(userData);
    }else alert('user name or password can not be empty');
  }


  render(){
	  return(
	    <div>
		    <h1>SignUp form</h1>
		    <form onSubmit={this.onSubmit} >
		      <div>
			      <label>User name </label><br/>
			      <input type="text" name="userName"   onChange={this.onChange}/>
			    </div> <br/>
		      <div>
						<label>Password </label><br/>
						<input type="text" name="password" onChange={this.onChange}/>
					</div>
					<br/>
					<button type="submit" onClick={this.handleClick}>Submit</button>
					<br/>
		    </form>
	    </div>
	  );
  }
}

const mapStateToProps = state=>{
  return {

  }
}
const mapDispatchToProps = dispatch =>{
  return {
    createUser:(userData)=>dispatch({type:'ADD_USER',userData})
  }
}
export default connect(mapStateToProps,mapDispatchToProps)(Signup);
