import React,{ Component } from "react";
import {connect } from 'react-redux';
import 'bootstrap/dist/css/bootstrap.min.css';

class PostForm extends Component{
  constructor(props){
	  super(props);
	  this.state = {
      title:null,
			body:null,
		//postImage:null
		}
		this.onChange = this.onChange.bind(this);
		this.onSubmit = this.onSubmit.bind(this);
  }

	// fileSelectedHandler = event =>{
	// 	alert(event)
	// 	this.setState({postImage:event.target.files[0]})
	// 	console.log(event)
	// }

  onChange(e){
	  this.setState({[e.target.name]:e.target.value});
  }

  onSubmit(e){
		e.preventDefault();
		if(this.state.title && this.state.body!=null){
			const post = {
				title:this.state.title,
				body : this.state.body,
			//	postImage:this.state.body
			}
			this.props.createPost(post);
		}
		else alert('Title or Body can not be Empty')
	}

  render(){
	  return(
	    <div>
				<center>
		    <h3>PostForm</h3>

					<form onSubmit={this.onSubmit} enctype="multipart/form-data">
		      <div>
			      <label>Title: </label><br/>
			      <input type="text" name="title"   onChange={this.onChange}/>
			    </div> <br/>
		      <div>
						<label>Body: </label><br/>
						<textarea type="text" rows="7" cols="50"name="body"  value={this.state.body} /><br/>
					</div>
					<label> Image </label><br/>
					<input type="file" name="postImage" onChange={this.fileSelectedHandler}/>
					<br/>
					<button type="submit" >Submit</button>
					<br/>
		    </form>
				</center>
	    </div>
	  );
  }
}

const mapStateToProps = state =>{
	return {
		items:state.items,
	}
}

const mapDispatchToProps =  dispatch =>{
	return{
		createPost:(post) =>dispatch({type:'ADD_POST',post})
	};
};

export default connect(mapStateToProps,mapDispatchToProps)(PostForm);
