import { takeLatest,takeEvery,put,all} from 'redux-saga/effects';


function* addPost(action)
{
    const post=JSON.stringify(action.post)
    alert(post)
    try{
      var url="http://localhost:3000/posts/"
      yield fetch(url,{
        method:'POST',
        body:post,
        headers:
        {
          'Content_type':'application/json'
        }
        })
        .then(res=>res.json())
        .then(response => console.log("POST Done",response))
    }
    catch(err)
    {
      alert('error'+err)
    }

    yield put({type:'ADD_NEW_POST_ASYNC',value:post})
}



function* Post()
{
    console.log("POST is Calling..")
    try{
      const data =  yield fetch('http://localhost:3000/posts/').then(res=>res.json());
      yield put({type:'POST_ASYNC',value:data})

    }
    catch(err){
      console.log(err);
    }
}



export default function* rootSaga(){
  console.log("root saga")
  yield all([
    yield takeLatest('POST',Post),
    yield takeEvery('ADD_POST',addPost)
  ])

}
