//import {FETCH_POST, NEW_POST} from '../actions/type';

const initialState = {
  items:[],
  item:{}

};

 const reducer =(state=initialState,action)=>{
  switch(action.type){
    case 'POST_ASYNC':
    console.log('reducer');
    return {
      ...state,
      items:action.value
    };
    case 'ADD_POST_ASYNC':
      return {
        ...state,
        item:action.value
      };

    default :
    return state;
}

}
export  default reducer;
